#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="I:\vuejsProjects\tov-template\node_modules\.pnpm\vitest@1.5.3_@types+node@20.12.7_lightningcss@1.24.1_terser@5.31.0\node_modules\vitest\node_modules;I:\vuejsProjects\tov-template\node_modules\.pnpm\vitest@1.5.3_@types+node@20.12.7_lightningcss@1.24.1_terser@5.31.0\node_modules;I:\vuejsProjects\tov-template\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/i/vuejsProjects/tov-template/node_modules/.pnpm/vitest@1.5.3_@types+node@20.12.7_lightningcss@1.24.1_terser@5.31.0/node_modules/vitest/node_modules:/mnt/i/vuejsProjects/tov-template/node_modules/.pnpm/vitest@1.5.3_@types+node@20.12.7_lightningcss@1.24.1_terser@5.31.0/node_modules:/mnt/i/vuejsProjects/tov-template/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../vitest/vitest.mjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../vitest/vitest.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../vitest/vitest.mjs" $args
  } else {
    & "node$exe"  "$basedir/../vitest/vitest.mjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
